package com.sia.tp3.cruce;import com.sia.tp3.Genes;import com.sia.tp3.Personaje;public class EnUnPunto implements InterfazCruce {    private EnDosPuntos enDosPuntos;    public EnUnPunto(final int primero) {        this.enDosPuntos = new EnDosPuntos(primero, Genes.CANTIDAD_GENES);    }    public int getPrimero() {        return enDosPuntos.getPrimero();    }    @Override    public void hacer(final Personaje personaje1, final Personaje personaje2) {        enDosPuntos.hacer(personaje1, personaje2);    }}