package com.sia.tp3.cruce;import com.sia.tp3.Genes;import com.sia.tp3.Personaje;public class EnUnPunto implements InterfazCruce {    private EnDosPuntos enDosPuntos;    public EnUnPunto(final int locus1) {        this.enDosPuntos = new EnDosPuntos(locus1, Genes.CANTIDAD_GENES);    }    public int getLocus1() {        return enDosPuntos.getLocus1();    }    @Override    public void hacer(final Personaje personaje1, final Personaje personaje2) {        enDosPuntos.hacer(personaje1, personaje2);    }}